function titleListener(){$("#category_title_input").keydown(function(t){13===t.keyCode&&$("#category_title_form").submit()})}function initializeForm(){$.fn.wysihtml5.defaultOptions.toolbar.image=!1,console.log($.fn.wysihtml5.defaultOptions),$(".question-form").wysihtml5()}function addQuestionListener(){$(".add-question").click(function(){return text=$.trim($(".question-form").val()),text&&(impact=$(".impact .active").text(),saveQuestions(text,impact)),!1})}function saveQuestions(t,e){$.ajax({url:"/creations/add_question",type:"POST",data:{text:t,qual:!0,quant:!0,impact:e}}).done(function(){$(".question-form").val(""),$("body").animate({scrollTop:$("body")[0].scrollHeight},500),console.log("success")}).fail(function(){console.log("error")}).always(function(){console.log("complete")})}jQuery(document).ready(function(){titleListener(),addQuestionListener()});