function checkBoxListener(){$("div.content").on("change","input.toGraph",function(){id=$(this).attr("id"),$(this).is(":checked")?update_selected(id,!0):update_selected(id,!1)})}function initializeBar(e,o){new Chart(o).Bar(e,null)}function update_selected(e,o){$.ajax({url:"/dashboard/update_selected",type:"POST",data:{collaborator_id:e,is_selected:o}}).done(function(){}).fail(function(){console.log("error")}).always(function(){console.log("complete")})}function loadCharts(){$.ajax({url:"/dashboard/load_charts",type:"GET"}).done(function(){}).fail(function(){console.log("error")}).always(function(){console.log("complete")})}jQuery(document).ready(function(){checkBoxListener()});