function numericInputListener(){max_range=10,$(".submissions").on("cut copy paste","input.response-rating",function(n){n.preventDefault()}),$(".submissions").on("keydown","input.response-rating",function(n){if(8==n.keyCode||46==n.keyCode)return!0;var e=n.keyCode?n.keyCode:n.which,t=$(n.target).val();return t*max_range+parseInt(String.fromCharCode(e))>max_range||isNaN(String.fromCharCode(e))?!1:void 0})}function clickListenerOutsideInput(){$(".submissions").on("focusout","input.response-rating",function(n){var e=n.target.id,t=$(n.target).val();saveRating(e,t)})}function saveRating(n,e){$.ajax({url:"/evaluation/save_rating",type:"POST",data:{submission_id:n,rating:e}}).done(function(){}).fail(function(){console.log("error")}).always(function(){console.log("complete")})}jQuery(document).ready(function(){numericInputListener(),clickListenerOutsideInput()});